function Slider(){const slides=document.querySelector(".slides");const slideElements=document.querySelectorAll(".slide");const prevButton=document.querySelector(".prev-btn");const nextButton=document.querySelector(".next-btn");if(!slides||slideElements.length===0){return}let currentIndex=0;const maxIndex=slideElements.length-1;function updateButtonStates(){if(currentIndex===0){prevButton.classList.add("disabled")}else{prevButton.classList.remove("disabled")}if(currentIndex===maxIndex){nextButton.classList.add("disabled")}else{nextButton.classList.remove("disabled")}}updateButtonStates();if(prevButton){prevButton.addEventListener("click",()=>{if(currentIndex>0){currentIndex-=1;slides.scrollTo({left:currentIndex*slideElements[0].offsetWidth,behavior:'smooth'});updateButtonStates()}})}if(nextButton){nextButton.addEventListener("click",()=>{if(currentIndex<maxIndex){currentIndex+=1;slides.scrollTo({left:currentIndex*slideElements[0].offsetWidth,behavior:'smooth'});updateButtonStates()}})}}document.addEventListener("DOMContentLoaded",Slider);